import { ADD_TO_CART,REMOVE_ITEM,SUB_QUANTITY,ADD_QUANTITY} from './action-types/cart-actions'

//add cart action
const addToCartBegin= (id)=>{
    return{
        type: ADD_TO_CART,
        id
    }
}
export function addToCart() {
    return (dispatch) => {
        dispatch(addToCartBegin());
        return fetch('http://localhost:3002/post-data')
                .then(res => res.json())
                .then(console.log('something happened'));
    }
}

//remove item action
export const removeItem=(id)=>{
    return{
        type: REMOVE_ITEM,
        id
    }
}
//subtract qt action
export const subtractQuantity=(id)=>{
    return{
        type: SUB_QUANTITY,
        id
    }
}
//add qt action
export const addQuantity=(id)=>{
    return{
        type: ADD_QUANTITY,
        id
    }
}
