import { ADD_ITEM,SUB_ITEM,SUB_QUANTITY,ADD_QUANTITY} from './action-types/cart-actions'

export const addToCart= (id)=>{
    return{
        type: ADD_ITEM,
        id
    }
}
export const removeItem=(id)=>{
    return{
        type: SUB_ITEM,
        id
    }
}
export const subtractQuantity=(id)=>{
    return{
        type: SUB_QUANTITY,
        id
    }
}
export const addQuantity=(id)=>{
    return{
        type: ADD_QUANTITY,
        id
    }
}
